SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_horas_compr_periodo (dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


dt_aux_w		timestamp;
hr_diarias_w		bigint;
ie_dia_semana_w		varchar(01);
ie_dia_semana_ww	varchar(01);
ie_feriado_w		varchar(01);


BEGIN

dt_aux_w	:= trunc(dt_inicial_p, 'dd');
hr_diarias_w	:= 0;

while(dt_aux_w <= dt_final_p) loop
	begin

	ie_dia_semana_w := to_char(pkg_date_utils.is_business_day(dt_aux_w,0));

	select	coalesce(max('S'), 'N')
	into STRICT	ie_feriado_w
	from 	feriado
	where 	dt_feriado = dt_aux_w
	and	cd_estabelecimento = cd_estabelecimento_p;


	If (ie_feriado_w = 'N') and
		--(ie_dia_semana_w in ('2', '3', '4', '5', '6')) then
		(ie_dia_semana_w  = 1) then  -- 1 = Dias de semana / 0 = Fim de semana
		begin
			ie_dia_semana_ww := to_char(pkg_date_utils.get_WeekDay(dt_aux_w));
			--if (ie_dia_semana_w = '2') or (ie_dia_semana_w = '6') then
			if (ie_dia_semana_ww = '2') or (ie_dia_semana_ww = '6') then
				hr_diarias_w	:= hr_diarias_w + 4;
			else
				hr_diarias_w	:= hr_diarias_w + 8;
			end if;
		end;
	end if;

	dt_aux_w	:= dt_aux_w + 1;

	end;
end loop;

RETURN hr_diarias_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
