CREATE OR REPLACE FUNCTION obter_horas_ocupacao ( dt_inicio_ref_p timestamp, dt_fim_ref_p timestamp, dt_entrada_unidade_p timestamp, dt_saida_unidade_p timestamp) RETURNS bigint AS $body$
DECLARE

dt_inicio_w	timestamp;
dt_fim_w	timestamp;
retorno_w	double precision;

BEGIN

if (dt_entrada_unidade_p < dt_inicio_ref_p) then
	dt_inicio_w := dt_inicio_ref_p;
else
	dt_inicio_w := dt_entrada_unidade_p;
end if;

if (dt_saida_unidade_p > dt_fim_ref_p) then
	dt_fim_w := dt_fim_ref_p;
else
	dt_fim_w := dt_saida_unidade_p;
end if;
retorno_w := (dt_fim_w - dt_inicio_w) / (86399/86400);
return retorno_w;

end;
