CREATE OR REPLACE FUNCTION obter_horas_prev_curativo ( nr_seq_dispositivo_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint ) RETURNS bigint AS $body$
DECLARE


qt_hora_w	bigint;


BEGIN

select	coalesce(max(qt_hora_curativo),0)
into STRICT	qt_hora_w
from	dispositivo_regra_troca
where	nr_seq_disp		=	nr_seq_dispositivo_p
and	cd_setor_atendimento	=	cd_setor_atendimento_p
and	cd_estabelecimento	=	cd_estabelecimento_p;

if (qt_hora_w = 0) then
	select	coalesce(max(qt_hora_curativo),0)
	into STRICT	qt_hora_w
	from	dispositivo_regra_troca
	where	nr_seq_disp		=	nr_seq_dispositivo_p
	and	cd_estabelecimento	=	cd_estabelecimento_p
	and	coalesce(cd_setor_atendimento::text, '') = '';
end if;

return	qt_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
