SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_horas_prev_disp_setor ( nr_seq_disp_pac_p bigint, nr_seq_disp bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		integer;
nr_seq_proc_interno_w	bigint;
cd_setor_atendimento_w	integer;
cd_estabelecimento_w	integer;


BEGIN

ds_retorno_w :=0;

if (nr_seq_disp_pac_p > 0) then

	select	coalesce(max(a.nr_seq_proc_interno),0)
	into STRICT	nr_seq_proc_interno_w
	from	dispositivo_proc_interno a,
		atend_pac_dispositivo b
	where	a.nr_seq_dispositivo	=	b.nr_seq_dispositivo
	and	b.nr_sequencia		=	nr_seq_disp_pac_p
	and	a.ie_acao in ('I','U');

	if (nr_seq_proc_interno_w > 0) then
		select	max(a.cd_setor_atendimento)
		into STRICT	cd_setor_atendimento_w
		from	atend_pac_disp_proc a
		where	a.nr_seq_proc_interno	= nr_seq_proc_interno
		and	a.nr_seq_disp_pac 	= nr_seq_disp_pac_p;
	end if;

end if;

if(cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '' AND cd_setor_atendimento_w >0) then

    	select cd_estabelecimento
   	 into STRICT cd_estabelecimento_w
   	 from setor_atendimento
   	 where cd_setor_atendimento = cd_setor_atendimento_w;

    	ds_retorno_w :=  obter_horas_prev_disp(nr_seq_disp, cd_setor_atendimento_w, cd_estabelecimento_w);

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
