CREATE OR REPLACE FUNCTION obter_horas_prev_disp ( nr_seq_dispositivo_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_hora_w 		bigint;
nr_seq_agrupamento_w 	bigint;

BEGIN

select 	coalesce(max(nr_seq_agrupamento),0)
into STRICT 	nr_seq_agrupamento_w
from 	setor_atendimento
where 	cd_setor_atendimento = cd_setor_atendimento_p;

select	coalesce(max(qt_hora),0)
into STRICT	qt_hora_w
from	dispositivo_regra_troca
where	nr_seq_disp		=	nr_seq_dispositivo_p
and	nr_seq_agrupamento	=	nr_seq_agrupamento_w
and	cd_estabelecimento	=	cd_estabelecimento_p;

if (qt_hora_w = 0) then
	select	coalesce(max(qt_hora),0)
	into STRICT	qt_hora_w
	from	dispositivo_regra_troca
	where	nr_seq_disp		=	nr_seq_dispositivo_p
	and	cd_setor_atendimento	=	cd_setor_atendimento_p
	and	cd_estabelecimento	=	cd_estabelecimento_p;
end if;

if (qt_hora_w = 0) then
	select	coalesce(max(qt_hora),0)
	into STRICT	qt_hora_w
	from	dispositivo_regra_troca
	where	nr_seq_disp		=	nr_seq_dispositivo_p
	and	cd_estabelecimento	=	cd_estabelecimento_p
	and	coalesce(cd_setor_atendimento::text, '') = '';
end if;

return	qt_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
