CREATE OR REPLACE FUNCTION obter_horas_sent_proj_migr ( nr_seq_projeto_p bigint, nr_seq_estagio_p bigint) RETURNS bigint AS $body$
DECLARE


qt_horas_projeto_w	double precision;
pr_projeto_w		double precision;
qt_horas_sent_migr_w	double precision;


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (nr_seq_estagio_p IS NOT NULL AND nr_seq_estagio_p::text <> '') then
	begin
	qt_horas_projeto_w := coalesce(proj_obter_qt_total_horas_cron(nr_seq_projeto_p,null,'T','E'),0);

	--if	(nr_seq_estagio_p in (1,2,12)) then -- cadastro do projeto / requisito definido / programação
