	if (nr_seq_estagio_p in (13,17,21)) then -- testes no desenvolvimento / testes no cliente / finalizado
		begin
		pr_projeto_w := 100;
		end;
	else
		begin
		pr_projeto_w := proj_obter_perc_cron(nr_seq_projeto_p,'R');
