CREATE OR REPLACE FUNCTION obter_horas_trabalhadas_pf_os ( nr_seq_os_p bigint, cd_pessoa_fisica_p text, dt_atividade_p timestamp) RETURNS bigint AS $body$
DECLARE


nm_usuario_w	varchar(15);
nr_horas_w	double precision := 0;


BEGIN
if (nr_seq_os_p IS NOT NULL AND nr_seq_os_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (dt_atividade_p IS NOT NULL AND dt_atividade_p::text <> '')then
	begin
	nm_usuario_w := obter_usuario_pf(cd_pessoa_fisica_p);

	if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
		begin
		select	dividir(coalesce(sum(qt_minuto),0),60)
		into STRICT	nr_horas_w
		from	man_ordem_serv_ativ
		where	nr_seq_ordem_serv = nr_seq_os_p
		and	trunc(dt_atividade,'dd') = trunc(dt_atividade_p,'dd')
		and	nm_usuario_exec = nm_usuario_w;
		end;
	end if;
	end;
end if;
return nr_horas_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
