CREATE OR REPLACE FUNCTION obter_horas_uteis_mes (dt_mes_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	bigint;


BEGIN

select (count(*) * 8) qt_hora_util_mes
into STRICT	vl_retorno_w
from (
	SELECT (to_date(to_char(dt_mes_p,'mm/yyyy'),'mm/yyyy')  + row_number() OVER () - 1) dt_dia_mes
	from tabela_sistema LIMIT ((to_char(last_day(dt_mes_p),'dd'))::numeric)) alias8
where    obter_se_dia_util(dt_dia_mes, cd_estabelecimento_p) = 'S';

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
