CREATE OR REPLACE FUNCTION obter_horas_validade_prescr_ps ( cd_setor_atendimento_p bigint, dt_prescricao_p timestamp) RETURNS bigint AS $body$
DECLARE


hr_inicio_setor_w		timestamp;
dt_prescrPronto_w		timestamp;
dt_inicio_prescr_w		timestamp;
nr_horas_diferen_w		integer;


BEGIN


	hr_inicio_setor_w	:=	Obter_Prim_Horario_Setor(cd_setor_atendimento_p);

	select to_date(to_char(dt_prescricao_p + 1,'dd/mm/yyyy') || ' ' || to_char(hr_inicio_setor_w,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
	into STRICT   dt_prescrPronto_w
	;

	nr_horas_diferen_w	:= 	Obter_Hora_Entre_datas(dt_prescricao_p,dt_prescrPronto_w);


return nr_horas_diferen_w;

end;
