CREATE OR REPLACE FUNCTION obter_hora_agenda_paciente (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_agenda_w        timestamp;

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')	then
	select  max(a.hr_inicio)
	into STRICT    dt_agenda_w
	from    agenda_paciente a
	where   a.nr_atendimento = nr_atendimento_p;

   	if (coalesce(dt_agenda_w::text, '') = '')	or (dt_agenda_w = '')	then
		select  max(b.dt_agenda)
		into STRICT	dt_agenda_w
		from	agenda_consulta b
		where	nr_atendimento = nr_atendimento_p;
	end if;
end if;

RETURN	dt_agenda_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
