CREATE OR REPLACE FUNCTION obter_hora_apap_gp (dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_hora_w	bigint;
ie_regra_apap_ganho_perda_w	varchar(10);

BEGIN

select	coalesce(max(ie_regra_apap_ganho_perda),'R')
into STRICT	ie_regra_apap_ganho_perda_w
from	parametro_medico
where	cd_estabelecimento = obter_estabelecimento_ativo;


if (ie_regra_apap_ganho_perda_w	= 'R') then

	nr_hora_w	:= (to_char(round(dt_referencia_p,'hh24'),'hh24'))::numeric;

else
	nr_hora_w	:= (to_char(trunc(dt_referencia_p,'hh24'),'hh24'))::numeric;

end if;

return	nr_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
