CREATE OR REPLACE FUNCTION obter_hora_atraso_laudo ( nr_seq_laudo_p bigint) RETURNS bigint AS $body$
DECLARE



dt_prev_entrega_w		timestamp;
dt_liberacao_w		timestamp;
qt_atraso_w			bigint;


BEGIN

qt_atraso_w			:= 0;
select	dt_prev_entrega,
		dt_liberacao
into STRICT		dt_prev_entrega_w,
		dt_liberacao_w
from		Laudo_paciente
where		nr_sequencia	= nr_seq_laudo_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') and (dt_prev_entrega_w IS NOT NULL AND dt_prev_entrega_w::text <> '') and
	((dt_liberacao_w - dt_prev_entrega_w) < 100) and (dt_liberacao_w > dt_prev_entrega_w) then
	qt_atraso_w	:= round((dt_liberacao_w - dt_prev_entrega_w) * 24);
end if;

if (coalesce(qt_atraso_w::text, '') = '') then
	qt_atraso_w := 0;
end if;

RETURN qt_atraso_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
