CREATE OR REPLACE FUNCTION obter_hora_controle (nr_Seq_dialise_p bigint, Qt_dif_tempo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_hora_w			timestamp;
nr_sequencia_w			bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	hd_controle
where	nr_seq_dialise = nr_seq_dialise_p;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	select	dt_controle + ((1/1440) * qt_dif_tempo_p)
	into STRICT	dt_hora_w
	from	hd_controle
	where	nr_sequencia = nr_sequencia_w;
end if;

return	dt_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
