CREATE OR REPLACE FUNCTION obter_hora_data_rat (nr_seq_rat_p bigint) RETURNS varchar AS $body$
DECLARE


result_w	varchar(255);
aux_w     	timestamp;


BEGIN

		aux_w := TO_DATE('00:00:00', 'hh24:mi:ss') + SUBSTR(obter_hrs_noturnas_rat(nr_seq_rat_p),1,5) /24;

		result_w := substr(to_char(aux_w, 'dd/mm/yyyy hh24:mi:ss'),12,8);



return result_w;

end;
