CREATE OR REPLACE FUNCTION obter_hora_duracao ( qt_minuto_duracao_p bigint) RETURNS varchar AS $body$
DECLARE


vl_aux_w		integer;
vl_hora_w		varchar(2);
ds_retorno_w	varchar(20);


BEGIN

if (qt_minuto_duracao_p > 0) then
	vl_aux_w	:= (qt_minuto_duracao_p - (TRUNC(qt_minuto_duracao_p / 60) * 60));

	if (vl_aux_w < 10) then
		vl_hora_w := '0' || to_char(vl_aux_w);
	else
		vl_hora_w := substr(to_char(vl_aux_w),1,2);
	end if;

	ds_retorno_w	:= TRUNC(qt_minuto_duracao_p / 60) ||':'|| vl_hora_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
