CREATE OR REPLACE FUNCTION obter_hora_entre_datas (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE

nr_horas_w	double precision;

BEGIN

nr_horas_w	:= abs((dt_final_p - dt_inicial_p) * 1440/60);

return	nr_horas_w;

end;
