SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_hora_hc_turno ( NR_SEQ_TURNO_P bigint, IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W	varchar(5);

/*
IE_OPCAO_P = I INICIAL
IE_OPCAO_P = F FINAL
*/
BEGIN
IF (NR_SEQ_TURNO_P IS NOT NULL AND NR_SEQ_TURNO_P::text <> '') THEN
	IF ( IE_OPCAO_P = 'I') THEN
	SELECT  MAX(TO_CHAR(HR_INICIAL,'HH24:MI'))
	INTO STRICT	DS_RETORNO_W
	FROM 	HC_TURNO
	WHERE 	NR_SEQUENCIA = NR_SEQ_TURNO_P;

	ELSE IF (IE_OPCAO_P = 'F') THEN

		SELECT  MAX(TO_CHAR(HR_FINAL,'HH24:MI'))
		INTO STRICT	DS_RETORNO_W
		FROM 	HC_TURNO
		WHERE 	NR_SEQUENCIA = NR_SEQ_TURNO_P;

		END IF;
	END IF;
END IF;

RETURN	DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
