CREATE OR REPLACE FUNCTION obter_hora_inicio_apap (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


hr_inicio_apap_w	varchar(5);
dt_inicio_apap_w timestamp;


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then

	select	coalesce(to_char(max(hr_inicio_apap+1/24),'hh24'),'07'),
		max(hr_inicio_apap)
	into STRICT	hr_inicio_apap_w,
		dt_inicio_apap_w
	from	parametro_medico
	where	cd_estabelecimento = cd_estabelecimento_p;

	if (dt_inicio_apap_w IS NOT NULL AND dt_inicio_apap_w::text <> '') and (to_char(dt_inicio_apap_w,'hh24') = '00') and (pkg_i18n.get_user_locale()='en_AU') then
			hr_inicio_apap_w  := to_char(dt_inicio_apap_w,'hh24');
	end if;

end if;

return hr_inicio_apap_w;

end;
$body$
