CREATE OR REPLACE FUNCTION obter_hora_ini_pri_horario_md ( hr_inicial_p text, ds_hora_inicio_w text, dt_inicio_prescr_w timestamp, dt_validade_prescr_w timestamp ) RETURNS varchar AS $body$
DECLARE

    hr_inicial_aux_w varchar(05) := hr_inicial_p;

BEGIN
    BEGIN
        IF ( ( hr_inicial_aux_w <> ds_hora_inicio_w ) OR (
            ( coalesce(ds_hora_inicio_w::text, '') = '' )
            AND (hr_inicial_aux_w IS NOT NULL AND hr_inicial_aux_w::text <> '')
        ) ) THEN
            IF
                ( ( pkg_date_utils.get_time(dt_inicio_prescr_w, coalesce(ds_hora_inicio_w, hr_inicial_aux_w)) < dt_inicio_prescr_w ) OR (
                pkg_date_utils.get_time(dt_inicio_prescr_w, coalesce(ds_hora_inicio_w, hr_inicial_aux_w)) > dt_validade_prescr_w ) )
                AND ( pkg_date_utils.get_time(dt_inicio_prescr_w, coalesce(ds_hora_inicio_w, hr_inicial_aux_w)) + 1 > dt_validade_prescr_w )
            THEN
                hr_inicial_aux_w := to_char(dt_inicio_prescr_w, 'hh24:mi');
            ELSE
                hr_inicial_aux_w := ds_hora_inicio_w;
            END IF;

        END IF;
    EXCEPTION
        WHEN OTHERS THEN
            hr_inicial_aux_w := ds_hora_inicio_w;
    END;

    RETURN hr_inicial_aux_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
