CREATE OR REPLACE FUNCTION obter_hora_turno_atend_farm ( nr_seq_turno_p bigint, nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w		smallint;
cd_setor_atendimento_w		integer;
ds_retorno_w			varchar(200);
hr_inicial_w			varchar(5);
hr_final_w			varchar(5);
cd_estabelecimento_ww		smallint := wheb_usuario_pck.get_cd_estabelecimento;
cd_setor_atendimento_ww		integer := wheb_usuario_pck.get_cd_setor_atendimento;

C01 CURSOR FOR
	SELECT	to_char(b.hr_inicial,'hh24:mi'),
		to_char(b.hr_final,'hh24:mi')
	from	regra_turno_disp_param b,
		regra_turno_disp a
	where	a.nr_sequencia		= b.nr_seq_turno
	and	a.cd_estabelecimento	= cd_estabelecimento_w
	and	a.nr_sequencia		= nr_seq_turno_p
	and (coalesce(b.cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w)
	order by coalesce(b.cd_setor_atendimento,0),
		to_char(b.hr_inicial,'hh24:mi');



BEGIN

select	coalesce(max(cd_setor_atendimento), cd_setor_atendimento_ww),
	coalesce(coalesce(max(cd_estabelecimento), cd_estabelecimento_ww),1)
into STRICT	cd_setor_atendimento_w,
	cd_estabelecimento_w
from	prescr_medica
where	nr_prescricao		= nr_prescricao_p;

OPEN C01;
LOOP
	FETCH C01 INTO
		hr_inicial_w,
		hr_final_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	hr_inicial_w	:= hr_inicial_w;
	hr_final_w	:= hr_final_w;
	end;
END LOOP;
CLOSE C01;

if (ie_opcao_p = 'I') then
	begin
	ds_retorno_w	:= replace(hr_inicial_w,'00:','24:');
	end;
elsif (ie_opcao_p = 'F') then
	begin
	ds_retorno_w	:= replace(hr_final_w,'00:','24:');
	end;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
