CREATE OR REPLACE FUNCTION obter_hora_turno_setor ( nr_seq_turno_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w		smallint;
cd_setor_atendimento_w		integer;
ds_retorno_w			varchar(200);
hr_inicial_w			varchar(5);
hr_final_w			varchar(5);

c01 CURSOR FOR
	SELECT	to_char(b.hr_inicial,'hh24:mi'),
		to_char(b.hr_final,'hh24:mi')
	from	regra_turno_disp_param b,
		regra_turno_disp a
	where	a.nr_sequencia		= b.nr_seq_turno
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.nr_sequencia		= nr_seq_turno_p
	and (coalesce(b.cd_setor_atendimento,cd_setor_atendimento_p)	= cd_setor_atendimento_p)
	order by coalesce(b.cd_setor_atendimento,0),
		to_char(b.hr_inicial,'hh24:mi');


BEGIN

open c01;
loop
	fetch c01 into
		hr_inicial_w,
		hr_final_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	hr_inicial_w	:= hr_inicial_w;
	hr_final_w	:= hr_final_w;
	end;
end loop;
close c01;

if (ie_opcao_p = 'I') then
	begin
	ds_retorno_w	:= replace(hr_inicial_w,'00:','24:');
	end;
elsif (ie_opcao_p = 'F') then
	begin
	ds_retorno_w	:= replace(hr_final_w,'00:','24:');
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
