CREATE OR REPLACE FUNCTION obter_hora_ult_setor ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



cd_ult_setor_w	bigint;
ds_retorno_w	bigint;
cd_setor_atendimento_w	bigint;
dt_entrada_setor_w	timestamp;
dt_ref_setor_w	timestamp;


BEGIN

Select	max(a.cd_setor_atendimento),
		max(a.dt_entrada_Unidade),
		max(clock_timestamp())
into STRICT	cd_ult_setor_w,
		dt_entrada_setor_w,
		dt_ref_setor_w
from 	atend_paciente_unidade a
where     a.cd_setor_atendimento = cd_setor_atendimento_p
and 	a.nr_atendimento = nr_atendimento_p
and		a.dt_entrada_Unidade = (SELECT	max(b.dt_entrada_Unidade)
								from	atend_paciente_unidade b
								where	a.nr_atendimento = b.nr_atendimento
								and     b.cd_setor_atendimento = cd_setor_atendimento_p)
								and     a.dt_saida_unidade is  null;

if (dt_entrada_setor_w IS NOT NULL AND dt_entrada_setor_w::text <> '') then
	ds_retorno_w := trunc(24* (dt_ref_setor_w - dt_entrada_setor_w));
end if;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '' AND ds_retorno_w <= 24) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
