CREATE OR REPLACE FUNCTION obter_hor_forcado_agecons (dt_agenda_p timestamp, hr_agenda_p text, cd_agenda_p bigint) RETURNS timestamp AS $body$
DECLARE


qt_hor_lf_w	bigint;
dt_retorno_w	timestamp;
dt_agenda_w	timestamp;
ds_retorno_w	varchar(30);


BEGIN
dt_agenda_w	:= to_date(to_char(dt_agenda_p,'dd/mm/yyyy')|| ' ' || hr_agenda_p|| ':00','dd/mm/yyyy hh24:mi:ss');

select	coalesce(max((to_char(dt_agenda,'ss'))::numeric ),0)+1
into STRICT	qt_hor_lf_w
from	agenda_consulta
where	cd_agenda = cd_agenda_p
and	to_date(to_char(dt_agenda,'dd/mm/yyyy hh24:mi')||':00','dd/mm/yyyy hh24:mi:ss') = dt_agenda_w;

dt_retorno_w := (dt_agenda_w + qt_hor_lf_w/86400);

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
