CREATE OR REPLACE FUNCTION obter_hor_item_sol_princ ( nr_seq_horario_p prescr_mat_hor.nr_sequencia%type, nr_seq_cpoe_p cpoe_material.nr_sequencia%type default null, cd_material_p material.cd_material%type default null, nr_seq_etapa_p prescr_mat_hor.nr_etapa_sol%type DEFAULT NULL) RETURNS PRESCR_MAT_HOR.NR_SEQUENCIA%TYPE AS $body$
DECLARE


nr_prescricao_w		prescr_medica.nr_prescricao%type;
cd_material_w		material.cd_material%type;
nr_seq_solucao_w	prescr_solucao.nr_seq_solucao%type;
nr_seq_horario_w	prescr_mat_hor.nr_sequencia%type;

BEGIN

if ((nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') and
	(nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') and
	(cd_material_p IS NOT NULL AND cd_material_p::text <> '') and
	(nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '')) then

	select	max(a.nr_prescricao) nr_prescricao,
			max(a.nr_seq_solucao) nr_seq_solucao
	into STRICT	nr_prescricao_w,
			nr_seq_solucao_w
	from	prescr_mat_hor a
	where	a.nr_sequencia = nr_seq_horario_p;
	
	select	max(a.cd_material) cd_material
	into STRICT	cd_material_w
	from	cpoe_material a
	where	a.nr_sequencia = nr_seq_cpoe_p;
	
	if (coalesce(cd_material_w, 0) > 0) then
		if (cd_material_w <> cd_material_p) then
			select	max(a.nr_sequencia) nr_seq_horario
			into STRICT	nr_seq_horario_w
			from	prescr_mat_hor a
			where	a.nr_prescricao = nr_prescricao_w
			and		a.nr_seq_solucao = nr_seq_solucao_w
			and		a.nr_etapa_sol = nr_seq_etapa_p
			and		a.cd_material = cd_material_w;
		else
			nr_seq_horario_w := nr_seq_horario_p;
		end if;
	end if;
end if;

return	nr_seq_horario_w;

end;
$body$
