SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_hor_jejum ( dt_coluna_p text, dt_filtro_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_coluna_w	        varchar(255);
dt_filtro_w			varchar(255);
dia_w				varchar(10);
dia_filtro_w		varchar(10);
mes_w				bigint;
ano_w				varchar(10);
hora_w				varchar(10);


BEGIN

if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (dt_filtro_p IS NOT NULL AND dt_filtro_p::text <> '') and (dt_filtro_p IS NOT NULL AND dt_filtro_p::text <> '')then

	if (ie_opcao_p = 'DHM') then
		dia_w  := substr(trim(both dt_coluna_p),1,2);
		hora_w := substr(trim(both dt_coluna_p),4,5)||':00';
		ano_w  := substr(trim(both dt_filtro_p),7,4);
		mes_w  := substr(trim(both dt_filtro_p),4,2);
		dia_filtro_w := substr(dt_filtro_p,1,2);

		if (dia_w < dia_filtro_w) then
			mes_w := mes_w +1;
		end if;

		ds_retorno_w := dia_w ||'/'||mes_w||'/'||ano_w||' '||hora_w;
	end if;

end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
