CREATE OR REPLACE FUNCTION obter_hor_min_espera (nr_minutos_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tempo_espera_w	varchar(20);
nr_horas_w		bigint;
nr_minutos_w		bigint;


BEGIN
nr_minutos_w := 0;
nr_horas_w   := 0;

nr_minutos_w	:= nr_minutos_p;

while(nr_minutos_w > 60) loop
	nr_horas_w := nr_horas_w + 1;
	nr_minutos_w := nr_minutos_w - 60;
end loop;

ds_tempo_Espera_w	:= lpad(nr_horas_w, 2, '0') || ':' || lpad(nr_minutos_w, 2, '0');

return	ds_tempo_espera_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
