CREATE OR REPLACE FUNCTION obter_hor_sol_grid ( nr_prescricao_p bigint, hr_prim_horario_p text, dt_status_p timestamp, ie_urgencia_p text) RETURNS varchar AS $body$
DECLARE


dt_Prim_hor_w		varchar(255);
dt_inicio_prescr_w	timestamp;


BEGIN

select	max(dt_inicio_prescr)
into STRICT	dt_inicio_prescr_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

select	max(to_char(obter_primeiro_hor_sol(obter_prim_horario_sol(hr_prim_horario_p,dt_status_p), dt_inicio_prescr_w, ie_urgencia_p), 'dd/mm/yyyy hh24:mi:ss'))
into STRICT	dt_Prim_hor_w
;

return	dt_Prim_hor_w;

end;
$body$
LANGUAGE PLPGSQL
