SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_hr_ini_prim_horario_md ( dt_prescricao_p timestamp, hr_prescricao_p text ) RETURNS varchar AS $body$
DECLARE

    hr_inicial_w  varchar(05);
    ie_dia_w      bigint;

BEGIN
    SELECT
        establishment_timezone_utils.startofday(dt_prescricao_p) - establishment_timezone_utils.startofday(clock_timestamp())
    INTO STRICT ie_dia_w
;

    IF ( pkg_date_utils.get_time(dt_prescricao_p, hr_prescricao_p) > dt_prescricao_p ) OR ( ie_dia_w > 0 ) THEN
        hr_inicial_w := hr_prescricao_p;
    ELSE
        hr_inicial_w := to_char(trunc(dt_prescricao_p, 'hh') + 1 / 24, 'hh24:mi');
    END IF;

    RETURN hr_inicial_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
