SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_icd_lado_tasy (ie_loc_3m_p text) RETURNS varchar AS $body$
DECLARE


    nr_seq_regra_conv_w bigint;
    ie_lado_w         varchar(50);


BEGIN
    -- obtém código da regra 3M
    select max(b.nr_seq_regra_conv)
    into STRICT    nr_seq_regra_conv_w
    from    intpd_eventos a,
            intpd_eventos_sistema b
    where   a.ie_evento = 64
        and a.nr_sequencia = b.nr_seq_evento;

    -- converte o valor recebido da 3M para Tasy
    if (ie_loc_3m_p IS NOT NULL AND ie_loc_3m_p::text <> '')then
        ie_lado_w := intpd_conv('DIAGNOSTICO_DOENCA', 'IE_LADO', ie_loc_3m_p, nr_seq_regra_conv_w, 'I', 'I');
    end if;

    return coalesce(ie_lado_w, '');

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_icd_lado_tasy (ie_loc_3m_p text) FROM PUBLIC;

