CREATE OR REPLACE FUNCTION obter_idade_agenda ( nr_seq_agenda_p bigint, cd_pessoa_fisica_p text, qt_idade_paciente_p bigint, dt_nascimento_pac_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_idade_w		varchar(15);
cd_pessoa_fisica_w	varchar(10);
qt_idade_paciente_w	smallint;
dt_nascimento_pac_w	timestamp;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	if (dt_nascimento_pac_p IS NOT NULL AND dt_nascimento_pac_p::text <> '') then
		ds_idade_w := obter_idade(dt_nascimento_pac_p,clock_timestamp(),'S');
	else
		select	obter_idade(dt_nascimento,clock_timestamp(),'S')
		into STRICT	ds_idade_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;
end if;

if (coalesce(ds_idade_w::text, '') = '') then
	ds_idade_w := qt_idade_paciente_p;
end if;

return coalesce(ds_idade_w,' ');

end;
$body$
