CREATE OR REPLACE FUNCTION obter_idade_conversao ( qt_ano_min_p bigint, qt_ano_min_mes_p bigint, qt_ano_min_dia_p bigint, qt_ano_max_p bigint, qt_ano_max_mes_p bigint, qt_ano_max_dia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;
qt_ano_min_w		double precision;
qt_ano_min_mes_w	double precision;
qt_ano_min_dia_w	double precision;
qt_ano_max_w		double precision;
qt_ano_max_mes_w	double precision;
qt_ano_max_dia_w	double precision;
dt_referencia_w		timestamp;


BEGIN

qt_ano_min_w		:= coalesce(qt_ano_min_p,0);
qt_ano_min_mes_w	:= coalesce(qt_ano_min_mes_p,0);
qt_ano_min_dia_w	:= coalesce(qt_ano_min_dia_p,0);
qt_ano_max_w		:= coalesce(qt_ano_max_p,0);
qt_ano_max_mes_w	:= coalesce(qt_ano_max_mes_p,0);
