CREATE OR REPLACE FUNCTION obter_idade_gest ( dt_ult_menstruacao_p timestamp, dt_referencia_p timestamp, ie_tipo_idade_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		smallint := 0;
qt_ig_sem_w			smallint;
qt_ig_dia_w			smallint;


BEGIN
begin
if (dt_ult_menstruacao_p IS NOT NULL AND dt_ult_menstruacao_p::text <> '') and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	select	round((TRUNC(dt_referencia_p) - trunc(dt_ult_menstruacao_p))/7),
			MOD((TRUNC(dt_referencia_p) - trunc(dt_ult_menstruacao_p)),7)
	into STRICT	qt_ig_sem_w,
			qt_ig_dia_w
	;

	if (ie_tipo_idade_p	= 'IGS') then
		qt_retorno_w	:= qt_ig_sem_w;
	else
		qt_retorno_w	:= qt_ig_dia_w;
	end if;

end if;
exception
	when others then
	qt_retorno_w	:= null;
end;
return qt_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
