CREATE OR REPLACE FUNCTION obter_idade_igc ( dt_atual_p timestamp, dt_nascimento_p timestamp, dt_nascimento_ig_p timestamp, qt_dias_ig_p bigint, qt_semanas_ig_p bigint) RETURNS varchar AS $body$
DECLARE

ds_semana		varchar(30) := obter_texto_dic_objeto(449706, wheb_usuario_pck.get_nr_seq_idioma,null);
ds_dia			varchar(30) := obter_texto_dic_objeto(449705, wheb_usuario_pck.get_nr_seq_idioma,null);
ds_retorno_w	varchar(250);
qt_diferenca_w	bigint;
qt_dias_total_w	bigint;
qt_semanas_w	bigint;
qt_dias_w		bigint;

BEGIN

if (dt_nascimento_ig_p IS NOT NULL AND dt_nascimento_ig_p::text <> '') and (qt_dias_ig_p IS NOT NULL AND qt_dias_ig_p::text <> '') and (qt_semanas_ig_p IS NOT NULL AND qt_semanas_ig_p::text <> '') then

	if (trunc(dt_nascimento_ig_p)	> trunc(dt_atual_p)) then

		qt_diferenca_w	:= trunc(dt_atual_p - dt_nascimento_p);

		qt_dias_total_w:= coalesce(qt_dias_ig_p,0) + coalesce(qt_semanas_ig_p,0) * 7;

		qt_dias_total_w	:= qt_dias_total_w + qt_diferenca_w;

		qt_semanas_w	:= trunc(dividir(qt_dias_total_w,7));
		qt_dias_w		:= mod(qt_dias_total_w,7);

		if (coalesce(qt_semanas_w,0)		> 0) then
			ds_retorno_w	:= ds_semana || ' '||qt_semanas_w;
		end if;

		if (coalesce(qt_dias_w,0)		> 0) then
			ds_retorno_w	:= ds_retorno_w||' ' || ds_dia ||' '||qt_dias_w;
		end if;


	else
		ds_retorno_w	:= obter_idade(dt_nascimento_ig_p,dt_atual_p,'S');
	end if;


end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
