CREATE OR REPLACE FUNCTION obter_idade_min_max_vulnerab (cd_pessoa_fisica_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


qt_retorno_w		varchar(1);


BEGIN

select coalesce(max('S'),'N')
	   into STRICT qt_retorno_w
from  vulnerabilidade
where nr_sequencia = nr_sequencia_p
and (ie_sexo = obter_sexo_pf(cd_pessoa_fisica_p, 'C') or coalesce(ie_sexo::text, '') = '')
and	((coalesce(qt_idade_min::text, '') = '' or coalesce(qt_idade_max::text, '') = '') or obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(), 'A') 
between qt_idade_min and qt_idade_max);

RETURN qt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
