SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_idade_paciente (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

    v_ano_nasc bigint;
    v_ano_atual bigint;

  
BEGIN
  
    
    IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN
      SELECT 
      
        EXTRACT(YEAR FROM pf.dt_nascimento),
        EXTRACT(YEAR FROM clock_timestamp()) 
        INTO STRICT  v_ano_nasc, 
              v_ano_atual
        
        FROM PESSOA_FISICA pf, ATENDIMENTO_PACIENTE atepac
        WHERE pf.cd_pessoa_fisica = atepac.cd_pessoa_fisica
        AND atepac.nr_atendimento = nr_atendimento_p;

    END IF;
      
    RETURN(v_ano_atual - v_ano_nasc);

  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_idade_paciente (nr_atendimento_p bigint) FROM PUBLIC;

