SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_idade_pac_agenda (cd_tipo_agenda_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
qt_idade_agenda_w	smallint;
qt_idade_meses_w	bigint;
dt_nascimento_w	timestamp;
dt_atual_w		timestamp;
ds_idade_pf_w		varchar(30);
ds_idade_w		varchar(255);


BEGIN
if (cd_tipo_agenda_p IS NOT NULL AND cd_tipo_agenda_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	if (cd_tipo_agenda_p = 2) then
		/* obter dados agenda */

		select	cd_pessoa_fisica,
			qt_idade_paciente,
			qt_idade_meses
		into STRICT	cd_pessoa_fisica_w,
			qt_idade_agenda_w,
			qt_idade_meses_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_agenda_p;

		/* calcular / gerar idade */

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			/* obter dados pf */

			select	coalesce(max(dt_nascimento), clock_timestamp()),
				coalesce(max(dt_obito), clock_timestamp())
			into STRICT	dt_nascimento_w,
				dt_atual_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;

			/* obter idade */

			/*OS 953663 - alterado de S para SNOVO de acordo com o comentario no obter_idade para calculo correto da idade do paciente*/

			select	obter_idade(dt_nascimento_w, dt_atual_w, 'SNOVO')
			into STRICT	ds_idade_pf_w
			;

			if (somente_numero(ds_idade_pf_w) > 0) then
				ds_idade_w := ds_idade_pf_w;
			else
				ds_idade_w := 0;
			end if;

		elsif (qt_idade_agenda_w IS NOT NULL AND qt_idade_agenda_w::text <> '') and (qt_idade_agenda_w > 0) then
			if (qt_idade_agenda_w > 1) then
				ds_idade_w := qt_idade_agenda_w || ' ' || wheb_mensagem_pck.get_texto(795516);

			else
				ds_idade_w := qt_idade_agenda_w || ' ' || wheb_mensagem_pck.get_texto(795537);
			end if;

			if (qt_idade_meses_w > 1) then
				ds_idade_w := ds_idade_w||' '|| qt_idade_meses_w || ' ' || wheb_mensagem_pck.get_texto(795517);

			elsif (qt_idade_meses_w <> 0) then
				ds_idade_w := ds_idade_w||' '|| qt_idade_meses_w || ' ' || wheb_mensagem_pck.get_texto(795545);
			end if;


		elsif (qt_idade_meses_w IS NOT NULL AND qt_idade_meses_w::text <> '') and (qt_idade_meses_w > 0) then
			if (qt_idade_meses_w > 1) then
				ds_idade_w := qt_idade_meses_w || ' ' || wheb_mensagem_pck.get_texto(795517);
			else
				ds_idade_w := qt_idade_meses_w || ' ' || wheb_mensagem_pck.get_texto(795545);
			end if;
		end if;

	elsif (cd_tipo_agenda_p = 3) then
		/* obter dados agenda */

		select	cd_pessoa_fisica,
			qt_idade_pac
		into STRICT	cd_pessoa_fisica_w,
			qt_idade_agenda_w
		from	agenda_consulta
		where	nr_sequencia = nr_seq_agenda_p;

		/* calcular / gerar idade */

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			/* obter dados pf */

			select	coalesce(max(dt_nascimento), clock_timestamp()),
				coalesce(max(dt_obito), clock_timestamp())
			into STRICT	dt_nascimento_w,
				dt_atual_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;

			/* obter idade */

			/*OS 953663 - alterado de S para SNOVO de acordo com o comentario no obter_idade para calculo correto da idade do paciente*/

			select	obter_idade(dt_nascimento_w, dt_atual_w, 'SNOVO')
			into STRICT	ds_idade_pf_w
			;

			if (somente_numero(ds_idade_pf_w) > 0) then
				ds_idade_w := ds_idade_pf_w;
			end if;

		elsif (qt_idade_agenda_w IS NOT NULL AND qt_idade_agenda_w::text <> '') and (qt_idade_agenda_w > 0) then
			if (qt_idade_agenda_w > 1) then
				ds_idade_w := qt_idade_agenda_w || ' ' || wheb_mensagem_pck.get_texto(795516);
			else
				ds_idade_w := qt_idade_agenda_w || ' ' || wheb_mensagem_pck.get_texto(795537);
			end if;
		end if;
	elsif (cd_tipo_agenda_p = 4) then

		select	cd_pessoa_fisica
		into STRICT	cd_pessoa_fisica_w
		from	oft_consulta
		where	nr_sequencia = nr_seq_agenda_p;

		/* calcular / gerar idade */

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			/* obter dados pf */

			select	coalesce(max(dt_nascimento), clock_timestamp()),
				coalesce(max(dt_obito), clock_timestamp())
			into STRICT	dt_nascimento_w,
				dt_atual_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;

			/* obter idade */

			/*OS 953663 - alterado de S para SNOVO de acordo com o comentario no obter_idade para calculo correto da idade do paciente*/

			select	obter_idade(dt_nascimento_w, dt_atual_w, 'SNOVO')
			into STRICT	ds_idade_pf_w
			;

			if (somente_numero(ds_idade_pf_w) > 0) then
				ds_idade_w := ds_idade_pf_w;
			end if;
		end if;
	end if;
end if;

return ds_idade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
