CREATE OR REPLACE FUNCTION obter_idade_param_prescr_onc ( qt_idade_min_p bigint, qt_idade_min_mes_p bigint, qt_idade_min_dia_p bigint, qt_idade_max_p bigint, qt_idade_max_mes_p bigint, qt_idade_max_dia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;
dt_referencia_w		timestamp;


BEGIN

if (ie_opcao_p = 'MIN') then
	begin
	dt_referencia_w	:= clock_timestamp() - qt_idade_min_dia_p;
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_idade_min_mes_p * -1,0);
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_idade_min_p * 12 * -1,0);
	end;
elsif (ie_opcao_p = 'MAX') then
	begin
	dt_referencia_w	:= clock_timestamp() - qt_idade_max_dia_p;
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_idade_max_mes_p * -1,0);
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_idade_max_p * 12 * -1,0);
	end;
end if;

qt_retorno_w    := pkg_date_utils.get_diffdate(dt_referencia_w,clock_timestamp(),'YEAR',0);

if (ie_opcao_p = 'MAX') and (qt_retorno_w = 0) then
	qt_retorno_w	:= 9999999999;
end if;

RETURN qt_retorno_w;

END;
