CREATE OR REPLACE FUNCTION obter_idade_pf ( cd_pessoa_fisica_p text, dt_atual_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_nascimento_w	timestamp;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select max(dt_nascimento)
	into STRICT	dt_nascimento_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;


	return obter_idade(dt_nascimento_w, dt_atual_p, ie_opcao_p);
else
	return null;
end if;

end;
$body$
