CREATE OR REPLACE FUNCTION obter_idade_regra_uso ( nr_sequencia_p bigint, ie_opcao_p text, ie_tipo_regra_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;
qt_ano_min_w		double precision;
qt_ano_min_mes_w	double precision;
qt_ano_min_dia_w	double precision;
qt_ano_max_w		double precision;
qt_ano_max_mes_w	double precision;
qt_ano_max_dia_w	double precision;
dt_referencia_w		timestamp;


BEGIN

if (ie_tipo_regra_p = 'P') then
	select	coalesce(max(qt_ano_min),0),
		coalesce(max(qt_ano_min_mes),0),
		coalesce(max(qt_ano_min_dia),0),
		coalesce(max(qt_ano_max),0),
		coalesce(max(qt_ano_max_mes),0),
		coalesce(max(qt_ano_max_dia),0)
	into STRICT	qt_ano_min_w,
		qt_ano_min_mes_w,
		qt_ano_min_dia_w,
		qt_ano_max_w,
		qt_ano_max_mes_w,
		qt_ano_max_dia_w
	from	convenio_regra_qtde_proc
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_tipo_regra_p = 'M') then
	select	coalesce(max(qt_ano_min),0),
		coalesce(max(qt_ano_min_mes),0),
		coalesce(max(qt_ano_min_dia),0),
		coalesce(max(qt_ano_max),0),
		coalesce(max(qt_ano_max_mes),0),
		coalesce(max(qt_ano_max_dia),0)
	into STRICT	qt_ano_min_w,
		qt_ano_min_mes_w,
		qt_ano_min_dia_w,
		qt_ano_max_w,
		qt_ano_max_mes_w,
		qt_ano_max_dia_w
	from	convenio_regra_qtde_mat
	where	nr_sequencia	= nr_sequencia_p;
end if;

if (ie_opcao_p = 'MIN') then
	begin
	dt_referencia_w	:= clock_timestamp() - qt_ano_min_dia_w;
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_ano_min_mes_w * -1,0);
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_ano_min_w * 12 * -1,0);
	end;
elsif (ie_opcao_p = 'MAX') then
	begin
	dt_referencia_w	:= clock_timestamp() - qt_ano_max_dia_w;
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_ano_max_mes_w * -1,0);
	dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,qt_ano_max_w * 12 * -1,0);
	end;
end if;

qt_retorno_w	:= clock_timestamp() - dt_referencia_w;

if (ie_opcao_p = 'MAX') and (qt_retorno_w = 0) then
	qt_retorno_w	:= 9999999999;
end if;

RETURN qt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
