CREATE OR REPLACE FUNCTION obter_idade_rn ( nr_atendimento_p bigint, nr_seq_nascimento_p bigint, dt_analise_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_idade_w	varchar(200);
dt_nascimento_w	timestamp;

BEGIN

select	coalesce(max(dt_nascimento),dt_analise_p)
into STRICT	dt_nascimento_w
from	nascimento
where	nr_sequencia	= nr_seq_nascimento_p
and	nr_atendimento	= nr_atendimento_p;

ds_idade_w	:= trunc(obter_idade(dt_nascimento_w,dt_analise_p,'DIA'));

return	ds_idade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
