CREATE OR REPLACE FUNCTION obter_idioma_adicional (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_W	varchar(2000);
ds_idioma_w	varchar(80);
ds_fluencia_w	varchar(60);

c01 CURSOR FOR
	SELECT	substr(obter_desc_idioma(NR_SEQ_IDIOMA),1,80) ds_idioma,
		substr(obter_valor_dominio(1343,IE_FLUENCIA),1,60) ds_fluencia
	from	pessoa_fisica_idioma
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;


BEGIN

open c01;
loop
fetch c01 into	ds_idioma_w,
		ds_fluencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_W := ds_retorno_W || ds_idioma_w ||' '||ds_fluencia_w||'; ';
end loop;

close c01;

RETURN ds_retorno_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
