SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_idioma_arquetipo ( nr_seq_arquetipo_p bigint) RETURNS bigint AS $body$
DECLARE


cd_idioma_principal_w	varchar(80);
vl_retorno_w		bigint;


BEGIN

if (nr_seq_arquetipo_p IS NOT NULL AND nr_seq_arquetipo_p::text <> '') then
	select	a.nr_sequencia
	into STRICT	vl_retorno_w
	from	ehr_arquetipo_header a
	where	a.nr_seq_arquetipo = nr_seq_arquetipo_p
	and	a.ds_language = 'ISO_639-1::en';

	if (coalesce(vl_retorno_w::text, '') = '') then
		select	a.cd_idioma_principal
		into STRICT	cd_idioma_principal_w
		from	ehr_arquetipo a
		where	a.nr_sequencia = nr_seq_arquetipo_p;

		if (cd_idioma_principal_w IS NOT NULL AND cd_idioma_principal_w::text <> '') then
			select	a.nr_sequencia
			into STRICT	vl_retorno_w
			from	ehr_arquetipo_header a
			where	a.nr_seq_arquetipo = nr_seq_arquetipo_p
			and	a.ds_language = cd_idioma_principal_w;
		end if;
	end if;
end if;

return coalesce(vl_retorno_w,1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
