CREATE OR REPLACE FUNCTION obter_id_peso_npt_p_formul_md (qt_peso_p bigint, qt_altura_p bigint, tp_genero_p text, qt_Dev_Formula_p bigint ) RETURNS bigint AS $body$
DECLARE

   qt_ideal_peso_w real;

BEGIN
    if ((qt_peso_p IS NOT NULL AND qt_peso_p::text <> '') and qt_peso_p > 0
            and (qt_altura_p IS NOT NULL AND qt_altura_p::text <> '') and qt_altura_p > 152.4) then
        if (tp_genero_p = 'M') then
            qt_ideal_peso_w := 50 + coalesce(qt_Dev_Formula_p,0);
        elsif (tp_genero_p = 'F') then
            qt_ideal_peso_w := 45.5 + coalesce(qt_Dev_Formula_p,0);
        end if;
    end if;
    return dividir_sem_round_md((qt_ideal_peso_w*100),100);

END;
