SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ie_alto_risco ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_alto_risco_w varchar(1);



BEGIN
ie_alto_risco_w := 'N';

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_seq_solucao_p,0) > 0) then

	select	coalesce(max(a.ie_alto_risco),'N')
	into STRICT	ie_alto_risco_w
	from	prescr_material a,
		prescr_solucao b
	where	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_sequencia_solucao	= nr_seq_solucao_p
	and	a.nr_sequencia_solucao	= b.nr_seq_solucao
	and	a.nr_prescricao	= b.nr_prescricao
	and	a.ie_alto_risco	= 'S';

end if;

return ie_alto_risco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
