CREATE OR REPLACE FUNCTION obter_ie_condicao ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type ) RETURNS varchar AS $body$
DECLARE

ie_condicao_w	pessoa_fisica_aux.ie_condicao%type;

BEGIN
	select coalesce(max(pfa.ie_condicao), 'N')
	into STRICT   ie_condicao_w
	from   pessoa_fisica_aux pfa
	where  pfa.cd_pessoa_fisica = cd_pessoa_fisica_p;
return ie_condicao_w;
end;
$body$
LANGUAGE PLPGSQL
