CREATE OR REPLACE FUNCTION obter_ie_legenda_news (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

								
qt_pontuacao_w					bigint;
ie_complexidade_assist_w		varchar(10);
ie_parametro3_w  				varchar(1);
ie_legenda_news_w				varchar(1);


BEGIN

select	max(QT_PONTUACAO),
		max(obter_parametro_3_news(nr_sequencia))
into STRICT	qt_pontuacao_w,
		ie_parametro3_w
from	escala_news a
where	nr_sequencia	= (	SELECT	max(x.nr_sequencia)
				from	escala_news x
				where	x.nr_atendimento	= nr_atendimento_p
				and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
				and	coalesce(x.dt_inativacao::text, '') = '');

if (qt_pontuacao_w >= 7) then
	ie_legenda_news_w :=  'A';

elsif (qt_pontuacao_w in ( 5, 6 ) or upper(ie_parametro3_w) = 'S') then
	ie_legenda_news_w :=  'M';

elsif (qt_pontuacao_w in ( 0, 1, 2, 3, 4 )) then
	ie_legenda_news_w :=  'B';
end if;

RETURN ie_legenda_news_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
