CREATE OR REPLACE FUNCTION obter_ie_permite_lote ( nr_projeto_p bigint, cd_perfil_p bigint ) RETURNS varchar AS $body$
DECLARE


  ie_permite_lote_w varchar(10);


BEGIN

  SELECT ie_permite_lote
  INTO STRICT ie_permite_lote_w
  FROM (SELECT coalesce((CD_PERFIL),0) CD_PERFIL,
      ie_permite_lote
    FROM TASY_PROJ_ASSIN_PERFIL c
    WHERE c.nr_seq_proj               = nr_projeto_p
    AND coalesce(c.CD_PERFIL, cd_perfil_p) = cd_perfil_p
    ORDER BY 1 DESC
    ) alias3 LIMIT 1;

  RETURN ie_permite_lote_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
