CREATE OR REPLACE FUNCTION obter_ie_saldo_caixa_js ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_saldo_caixa_w	smallint;
qt_saldo_transacao_w	bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_saldo_transacao_w
	from	transacao_financeira
	where	nr_sequencia = nr_sequencia_p;

	if (qt_saldo_transacao_w > 0) then
		select	CASE WHEN ie_caixa='M' THEN 1 WHEN ie_caixa='D' THEN 2 WHEN ie_caixa='A' THEN 3 WHEN ie_caixa='N' THEN 4 WHEN ie_caixa='T' THEN 5 END
		into STRICT	ie_saldo_caixa_w
		from	transacao_financeira
		where	nr_sequencia = nr_sequencia_p;
	end if;
	end;
end if;

return	ie_saldo_caixa_w;

end;
$body$
LANGUAGE PLPGSQL
