CREATE OR REPLACE FUNCTION obter_ie_status_exame_acesso ( nr_seq_exame_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

/**
	Retorna o status do exame na tabela de acessos dos exames via web
	Juliane Menin - 12/10/2007
*/
ie_retorno_w		varchar(1);
seq_result_lab_w		bigint;

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select Obter_seq_result_laboratorio(nr_seq_exame_p, nr_prescricao_p)
	into STRICT seq_result_lab_w
	;
else
	seq_result_lab_w	:=  nr_seq_exame_p;

end if;

if ( seq_result_lab_w = 0 ) then
	ie_retorno_w := 3;
else
	select 	coalesce(max(ie_status), 3)
	into STRICT   	ie_retorno_w
	from 	result_laboratorio_acesso
	where 	nr_seq_result_lab = seq_result_lab_w
        	and	nr_sequencia = (	SELECT max(a.nr_sequencia) from result_laboratorio_acesso a
                            			where  a.nr_seq_result_lab = seq_result_lab_w);
end if;
return ie_retorno_w;

END;
$body$
